var id_pays_selected = 66;
function selectItem(targetEl, selected) {
	$(targetEl).val(selected);
}
function pays_change_regions(f_id_pays, f_id_region, f_reload, f_ville_no_france) {
	var l_pays_id = f_id_pays
	var l_region_id_sel = f_id_region;
	if (f_reload) {
		$('#prop3e').html(g_options_pays);
		if ('' != id_pays_selected) {
			if ($.browser.msie) {
				setTimeout("selectItem('#prop3e', '" + id_pays_selected + "');", 1);
			} else {
				$('#prop3e').val(id_pays_selected);
			}
			l_pays_id = id_pays_selected;
		} else {
			if ($.browser.msie) {
				setTimeout("selectItem('#prop3e', 66);", 1);
			} else {
				$('#prop3e').val(66);
			}
			l_pays_id = 66;
		}
	} else {
		l_pays_id = $('#prop3e').val();
	}
	if (id_pays_selected != l_pays_id) {
		$('#prop3a').val('');
		$('#prop3b').val('');
		$('#prop3c').val('');
		$('#prop3d').val('');
		$('#prop3t').val('');
		$('#prop3v').val('');
	}
	id_pays_selected = l_pays_id;
	if (g_options_regions[l_pays_id]) {
		$('#prop3f').html('<option value="">Régions</option>' + g_options_regions[l_pays_id]);
		$('#prop3f').show();
		$('#s-region').show();
		$('#lprop3f').show();
	} else {
		$('#prop3f').hide();
		$('#s-region').hide();
		$('#lprop3f').hide();
	}
	if (l_pays_id == f_id_pays) {
		if ($.browser.msie) {
			setTimeout("selectItem('#prop3f', '" + f_id_region + "');", 1);
		} else {
			$('#prop3f').val(f_id_region);
		}
	} else {
		if ($.browser.msie) {
			setTimeout("selectItem('#prop3f', '');", 1);
		} else {
			$('#prop3f').val('');
		}
	}
	if (l_pays_id == 66) {
		$('#s-dept_rencontres').show();
		if (f_ville_no_france) {
			$('#prop3t').val('');
			$('#s-ville').hide();
		}
	} else {
		$('#s-dept_rencontres').hide();
		if (f_ville_no_france) {
			$('#s-ville').show();
		}
	}
	if (g_pays_communes[l_pays_id]) {
		$('#s-cp').show();
	} else {
		$('#s-cp').hide();
	}
}
